    token,
    username,
    roles,
    issued_at
) VALUES (
    #{token},
    #{username},
    #{roles},
    #{issuedAt}
)

mapper.delete:
/* SQL_ID : kr.centero.common.auth.mapper.UserTokenMapper.deleteByUsername */
DELETE
FROM tb_token
WHERE username = #{username}
