mapper.select:
/* SQL_ID : kr.centero.common.user.mapper.UserMapper.findAllByCond */ SELECT tu.user_id, tu.username, tu.password, tu.email, tr.role_name FROM tb_user tu INNER JOIN tb_user_role tur ON tur.user_id = tu.user_id INNER JOIN tb_role tr ON tr.role_id = tur.role_id
AND tu.user_id = #{userId}
tu.username = #{username} AND
tu.email = #{email}
tr.role_name = #{roleName}
ORDER BY tu.username, tr.role_name

/* SQL_ID : kr.centero.common.user.mapper.UserMapper.findUserByCond */ SELECT tu.user_id, tu.username, tu.password, tu.email, tr.role_name FROM tb_user tu INNER JOIN tb_user_role tur ON tur.user_id = tu.user_id INNER JOIN tb_role tr ON tr.role_id = tur.role_id
AND tu.user_id = #{userId}
tu.username = #{username} AND
tu.email = #{email}
tr.role_name = #{roleName}
ORDER BY tu.username, tr.role_name

mapper.insert:
/* SQL_ID : kr.centero.common.user.mapper.UserMapper.save */
INSERT INTO tb_user (username, password, email)
VALUES (#{username}, #{password}, #{email})

mapper.update:
/* SQL_ID : kr.centero.common.user.mapper.UserMapper.update */
UPDATE tb_user
SET username = #{username},
    password = #{password},
    email = #{email}
WHERE user_id = #{userId}
